import { PRODUCT, PRICE } from '../actions';

const INITIAL_STATE = {
  product: {}
};

export default function (state = INITIAL_STATE, action) {
  switch (action.type) {
  case PRODUCT:
    return {
      ...state, action,
    };
  default:
    return state;
  }
}
