import { PRODUCT, PRICE } from '../actions';

const INITIAL_STATE = {
  price: 0,
};

export default function (state = INITIAL_STATE, action) {
  switch (action.type) {
  case PRODUCT:
    return {
      ...state, product: action.product,
    };
  case PRICE: 
    return{
      ...state, price: action.price + state.price,
    };
  default:
    return state;
  }
}
