import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';
import * as api from '../services/api';
import ProductCard from './ProductCard';
import Category from '../Category';

class Search extends Component {
  constructor() {
    super();

    this.handleClick = this.handleClick.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.handleCategory = this.handleCategory.bind(this);
    this.handleClickCategory = this.handleClickCategory.bind(this);

    this.state = {
      searchInput: '',
      categoryInput: '',
      products: {},
      seachDone: false,
      sliderX: 0,
    };
  }

  handleChange({ target }) {
    this.setState({ searchInput: target.value });
  }

  goLeft() {
    const additionalX = 110;
    const mintranslateX = 0;
    const maxtranslateX = 440;
    const { x } = this.state;
    if (x === mintranslateX) this.setState({ x: x - maxtranslateX });
    else this.setState({ x: x + additionalX });
  }

  goRight() {
    const additionalX = 110;
    const maxtranslateX = 440;
    const { x } = this.state;
    if (x === -maxtranslateX) this.setState({ x: x + maxtranslateX });
    else this.setState({ x: x - additionalX });
  }

  async handleClick() {
    const { searchInput, categoryInput } = this.state;

    this.setState({ seachDone: true }, async () => {
      const productsList = await
      api.getProductsFromCategoryAndQuery(categoryInput, searchInput);
      this.setState({ products: productsList });
    });
  }

  handleClickCategory(categoryId) {
    this.setState({
      categoryInput: categoryId,
      seachDone: true,
    });
  }

  async handleCategory(category) {
    const { searchInput } = this.state;
    const resultado = await api.getProductsFromCategoryAndQuery(category, searchInput);
    this.setState({
      products: resultado,
    });
  }

  render() {
    const { searchInput, products, seachDone } = this.state;
    const { addItemCart } = this.props;
    const zero = 0;
    return (
      <div>
        <header className="header">
          <div className="form-div">
            <form className="searchForm">
              <i class="fas fa-search search-button"
                onClick={ this.handleClick }
              ></i>
              <input
                className="search-input"
                id="home-initial-input"
                type="text"
                data-testid="query-input"
                placeholder="Digite aqui o termo da sua busca"
                value={ searchInput }
                onChange={ this.handleChange }
              />
                <Link data-testid="shopping-cart-button" to="/ShoppingCart">
                {/* https://fontawesome.com/icons/cart-plus?style=solid */}
                <i class="fas fa-cart-plus"></i>
              </Link>
            </form>
          </div>
        </header>
        <div className="center-container">
          <Category
            handleCategory={ this.handleCategory }
            handleClickCategory={ this.handleClickCategory }
          />
          <div className="search-container">
            <div className="product-container">
              <button type="button" id="goLeft" onClick={ this.goLeft }>
                <i className="fas fa-chevron-left" />
              </button>
              {!seachDone && (
                <p data-testid="home-initial-message">
                  Digite algum termo de pesquisa ou escolha uma categoria.
                </p>
              )}
              {Object.keys(products).length !== zero
              && seachDone
              && products.results.map((item) => (
                <ProductCard
                  addItemCart={ addItemCart }
                  product={ item }
                  key={ item.title }
                />
              ))}
              <button type="button" id="goRight" onClick={ this.goRight }>
                <i className="fas fa-chevron-right" />
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

Search.propTypes = { addItemCart: PropTypes.string.isRequired };
export default Search;
