import React, { Component } from 'react';
import * as api from './services/api';

class Category extends Component {
  constructor() {
    super();
    this.state = {
      categories: [],
      category: '',
      y: 0,
    };
    this.handleClick = this.handleClick.bind(this);
  }

  async componentDidMount() {
    this.fetchCategories();
  }

  async fetchCategories() {
    const response = await api.getCategories();
    this.setState({
      categories: response,
    });
  }

  goUp() {
    const additionalX = 110;
    const mintranslateX = 0;
    const maxtranslateX = 440;
    const { x } = this.state;
    if (x === mintranslateX) this.setState({ x: x - maxtranslateX });
    else this.setState({ x: x + additionalX });
  }

  goDown() {
    const additionalX = 110;
    const maxtranslateX = 440;
    const { x } = this.state;
    if (x === -maxtranslateX) this.setState({ x: x + maxtranslateX });
    else this.setState({ x: x - additionalX });
  }

  handleClick({ target }) {
    const { handleCategory, handleClickCategory } = this.props;
    const targetId = target.id;
    handleClickCategory(targetId);
    this.setState({
      category: targetId,
    }, () => {
      const { category } = this.state;
      handleCategory(category);
    });
  }

  render() {
    const { categories } = this.state;
    return (
      <div className="category-container" >
        <button type="button" id="goUp" onClick={ this.goUp }>
          {/* https://fontawesome.com/icons/chevron-up?style=solid */}
          <i class="fas fa-chevron-up"></i>
        </button>
        {categories.map((categoria, id) => (
          <div className="category-element" key={id}>
            <label>
              <input
                htmlFor={id}
                data-testid="category"
                type="checkbox"
                id={categoria.id}
                onClick={ this.handleClick }
              />
              {categoria.name}
            </label>
          </div>
        ))}
        <button type="button" id="goDown" onClick={ this.goDown }>
            {/* https://fontawesome.com/icons/chevron-down?style=solid */}
            <i class="fas fa-chevron-down"></i>
        </button>
      </div>
    );
  }
}

export default Category;
