import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
class ToogleCart extends Component {
  constructor() {
    super();
    this.state = {
      click: true,
    };
  }

  componentDidUpdate(prevState) {
    const { stateProduct} = this.props;
    const { click } = this.state;
    if(stateProduct && click) {
      this.addQuantity(prevState);
    }
  }

  addQuantity (prevState) {
    console.log(prevState)
    const { stateProduct} = this.props;
    const id = stateProduct[0].id
    this.setState({ [stateProduct[0]]: prevState + 1 })
    this.setState({click: false})
  }

  render() {
    const { cartList, stateProduct, statePrice} = this.props;
    const reajustedPrice = Math.round((statePrice + Number.EPSILON) * 100) / 100
    return (
      <div className="toogle-shoppingcart-container ">
        <div className="toogle-cart-header">
          {/* https://fontawesome.com/icons/shopping-cart?style=solid */}
          <i class="fas fa-shopping-cart"></i>
          <span>Carrinho de Compras</span>
        </div>
        {/* https://fontawesome.com/icons/box-open?style=solid */}
        {(cartList && cartList.length < 1) &&
          <div className="toogle-shoppingcart-empty">
            <i class="fas fa-box-open"></i>
            <p>Seu Carrinho est√° Vazio</p>
          </div>}
        <div className="toogle-center-container">
        {(cartList && cartList.length > 0)
          && cartList.map(element => (
            <div key={element.product.id} className="cart-item">
              <img src={element.product.thumbnail}/>
              <span className="toogle-title">{element.title}</span>
              <div className="toogle-quantity">
                {/* https://fontawesome.com/icons/minus?style=solid */}
                <i class="fas fa-minus"></i>
                <span>{element.product.quantity}</span>
                {/* https://fontawesome.com/icons/plus?style=solid */}
                <i class="fas fa-plus" onClick={ () =>  this.setState({click: true})}></i>
              </div>
              <span className="toogle-price">
                {`R$ ${element.product.price}`}
              </span>
            </div>
        ))}
        </div>
        {(cartList && cartList.length > 0) &&
          <div className="total">
            <span>
              { `Valor total da Compra: R$ ${reajustedPrice}` }
            </span>
            <button >
              Finalizar Compra
            </button>
          </div>
        }
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  stateProduct: state.product.product,
  statePrice: state.price.price,
});

export default connect(mapStateToProps)(ToogleCart);
