import React, { Component } from 'react';
class ToogleCart extends Component {
  constructor() {
    super();
    this.state = {
      totalPrice: 0,
    }
    this.totalPrice = this.totalPrice.bind(this);
  }

  componentDidUpdate() {

  }

  totalPrice() {
    // const { cartList } = this.props;
    // const totalPrice = cartList.map(element => (
    //   console.log (element.product.price)
    // ))
    // return totalPrice
  }

  render() {
    const { cartList } = this.props;
    const { totalPrice } = this.state;
    return (
      <div className="toogle-shoppingcart-container">
        <div className="toogle-cart-header">
          {/* https://fontawesome.com/icons/shopping-cart?style=solid */}
          <i class="fas fa-shopping-cart"></i>
          <span>Carrinho de Compras</span>
        </div>
        {/* https://fontawesome.com/icons/box-open?style=solid */}
        {(cartList && cartList.length < 1) &&
          <div className="toogle-shoppingcart-empty">
            <i class="fas fa-box-open"></i>
            <p>Seu Carrinho est√° Vazio</p>
          </div>}
        {(cartList && cartList.length > 0)
          && cartList.map(element => (
            <div key={element.product.id} className="cart-item">
              <img src={element.product.thumbnail}/>
              <span className="toogle-title">{element.product.title}</span>
              <div className="toogle-quantity">
                {/* https://fontawesome.com/icons/minus?style=solid */}
                <i class="fas fa-minus"></i>
                <span>{element.product.quantity}</span>
                {/* https://fontawesome.com/icons/plus?style=solid */}
                <i class="fas fa-plus"></i>
              </div>
              <span className="toogle-price">
                {`R$ ${element.product.price}`}
              </span>
            </div>
        ))}
        <span>{ totalPrice }</span>
      </div>
    );
  }
}

export default ToogleCart;
