import { CART, PRODUCT } from '../actions';

const INITIAL_STATE = {
  product: [],
  id: [],
  cart: []
};

export default function (state = INITIAL_STATE, action) {
  switch (action.type) {
  case PRODUCT:
    return {
      ...state, product: action.product,
      id: [...state.id, action.id ],
    };
  case CART:
    return {
      ...state,
      cart: [...state.cart, action.cartItem]
    }
  case REMOVE:
    return {
      cart: [action.cartItem]
    }
  default:
    return state;
  }
}
