import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';

class ProductCard extends Component {
  constructor() {
    super();

    this.addItemsToCart = this.addItemsToCart.bind(this);
    this.state = {
      quantity: 1,
      total: 0,
    };
  }

  addItemsToCart() {
    const { product, addItemCart } = this.props;
    const { title, thumbnail, price, id } = product;
    const { quantity, total } = this.state;
    this.setState({
      quantity,
      total: price + total,
    });
    addItemCart({ product: { title, thumbnail, price, id, quantity } });
  }

  render() {
    const { product, x } = this.props;
    const { title, thumbnail, price, id } = product;
    return (
      <Link
      data-testid="product-detail-link"
      to={ { pathname: `/ProductDetails/${id}`, state: product } }
      >
        <section
        key={ title }
        data-testid="product"
        className="product-content"
        style={ { transform: `translateY(${x}%)` } }
        >
          <div className="img-div">
            <img className="img" src={ thumbnail } alt={ title } />
          </div>
          <div className="product-details-div">
            <span>{title}</span>
          </div>
          <div className="addToCart">
            <span style={{marginLeft: '10px'}}>R$</span>
            <span className="price">{`${price}`}</span>
            <button
              type="button"
              className="add-button"
              data-testid="product-add-to-cart"
              onClick={ this.addItemsToCart }
            >
              {/* https://fontawesome.com/icons/plus-square?style=solid */}
              <i class="fas fa-plus-square"></i>
            </button>
          </div>
        </section>
      </Link>
    );
  }
}

ProductCard.propTypes = {
  product: PropTypes.shape({
    title: PropTypes.string,
    thumbnail: PropTypes.string,
    price: PropTypes.number,
    id: PropTypes.string,
  }).isRequired,
  addItemCart: PropTypes.string.isRequired,
};
export default ProductCard;
