import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import { productPrice, productItem } from '../actions';
import ProductDetails from '../pages/ProductDetails';

class ProductCard extends Component {
  constructor() {
    super();
    this.addItemsToCart = this.addItemsToCart.bind(this);
    this.state = {
      quantity: 1,
      total: 0,
      details: false,
    };
    this.renderDetails = this.renderDetails.bind(this);
  }

  addItemsToCart() {
    const { product, addItemCart, dispatchPrice, dispatchProduct } = this.props;
    const { title, thumbnail, price, id } = product;
    const { quantity } = this.state;
    dispatchPrice(price)
    dispatchProduct(product)
    addItemCart({ product: { title, thumbnail, price, id, quantity } }, price);
  }

  renderDetails() {
    const { details } = this.state;
    this.setState({ details: !details })
  }


  render() {
    const { product, x } = this.props;
    const { title, thumbnail, price, id } = product;

    return (
      <section
      key={ title }
      data-testid="product"
      className="product-content"
      style={ { transform: `translateY(${x}%)` } }
      onClick={ () => this.renderDetails() }
      >

      {/* <Link
        data-testid="product-detail-link"
        to={ { pathname: `/ProductDetails/${id}`, state: product } }
      ></Link> */}
        <div className="img-div">
          <img className="img" src={ thumbnail } alt={ title } />
        </div>
      <div className="product-details-div"
      style={{width:'196px', textAlign:'left'}}>
        <span>{title}</span>
      </div>
        <div className="addToCart">
          <span style={{marginLeft: '10px'}}>R$</span>
          <span className="price">{`${price}`}</span>
          <button
            type="button"
            className="add-button"
            data-testid="product-add-to-cart"
            onClick={ this.addItemsToCart }
          >
            {/* https://fontawesome.com/icons/plus-square?style=solid */}
            <i class="fas fa-plus-square"></i>
          </button>
        </div>
      </section>
    );
  }
}

ProductCard.propTypes = {
  product: PropTypes.shape({
    title: PropTypes.string,
    thumbnail: PropTypes.string,
    price: PropTypes.number,
    id: PropTypes.string,
  }).isRequired,
  addItemCart: PropTypes.string.isRequired,
};

const mapDispatchToProps = (dispatch) => ({
  dispatchPrice: (price) => dispatch(productPrice(price)),
  dispatchProduct: (product) => dispatch(productItem(product))
});

export default connect(null, mapDispatchToProps)(ProductCard);
