import { DETAILS } from '../actions';

const INITIAL_STATE = {
  toogle: false,
  product: {}
};

export default function (state = INITIAL_STATE, action) {
  switch (action.type) {
  case DETAILS: 
    return {
      ...state, price: action.price + state.price,
    };
  default:
    return state;
  }
}
