import React from 'react';
import { Link } from 'react-router-dom';
import propTypes from 'prop-types';
import boleto from '../img/boleto.jpeg';
import cartao from '../img/cartao.jpeg';
import { connect } from 'react-redux';
import { renderConfirmation } from '../actions';
import Confirmation from '../components/Confirmation';

class Checkout extends React.Component {
  constructor() {
    super();
    this.state = {
      render: false,
    };
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleSubmit(event) {
    event.preventDefault();
    const data = new FormData(event.target);

    fetch('/api/form-submit-url', {
      method: 'POST',
      body: data,
    });
  }

  render() {
    const estados = ['AC','AL', 'AP', 'AM', 'BA', 'CE', 'DF', 'ES', 'GO', 'MA', 'MT','MS', 'MG', 'PA', 'PB', 'PR', 'PE', 'PI', 'RJ', 'RN', 'RS', 'RO', 'RR', 'SC', 'SP', 'SE', 'TO'];
    const { products, renderConfirm, isToRenderConfirmation } = this.props;
    return (
      <div className="checkout-container">
        { isToRenderConfirmation && <Confirmation products={ products } /> }
        <div className="checkout-container-2">
          <div className="checkout-top-content">
            <h3>Revise seus Produtos</h3>
            {products.map((item) => (
              <div key={ this.id }>
                <div className="checkout-product-container">
                  <img src={ item.product.thumbnail } alt="imagem do procuto" />
                  <h3>{item.product.title}</h3>
                  <span>R$</span>
                  <h4>{item.product.price}</h4>
                </div>
                <hr className="checkout-hr" />
              </div>
            ))}
          </div>
          <form className="form-container" onSubmit={ this.handleSubmit }>
            <h3>Informações do Comprador</h3>
            <div className="inputs-div">
              <div className="form-top-content">
                <input type="text" placeholder="Nome Completo" />
                <input type="number" placeholder="CPF" />
                <input type="text" placeholder="Email" />
                <input type="number" placeholder="Telefone" />
              </div>
              <div className="form-middle-content">
                <input type="number" placeholder="CEP" />
                <input type="text" placeholder="Endereço" />
              </div>
              <div className="form-bottom-content">
              <input type="text" placeholder="Complemento" />
              <input type="number" placeholder="Número" />
              <input type="text" placeholder="Cidade" />
              <select name="estado" >
                <option value="" selected="selected">Selecione</option>
                {estados.map(estado => <option>{estado}</option> )}
              </select>
            </div>
            </div>
          </form>
          <div className="payment-container">
            <h3>Método de pagamento</h3>
            <div className="payment-methods">
              <div>
                <p>Boleto</p>
                <div className="boleto-div">
                  <label htmlFor="boleto">
                    <input id="boleto" type="radio" name="payment" />
                  </label>
                  <img
                    src={ boleto }
                    width="100"
                    alt="imagem de boleto"
                  />
                </div>
              </div>
              <div className="creditcard">
                <p>Cartão de Crédito</p>
                <div className="card-div">
                  <label htmlFor="visa">
                    <input id="visa" type="radio" name="payment" />
                    Visa
                  </label>
                  <img src={ cartao } width="100" alt="imagem de cartao" />
                  <label htmlFor="mastercard">
                    <input id="mastercard" type="radio" name="payment" />
                    Mastercard
                  </label>
                  <img src={ cartao } width="100" alt="imagem de cartao" />
                  <label htmlFor="elo">
                    <input id="elo" type="radio" name="payment" />
                    Elo
                  </label>
                  <img src={ cartao } width="100" alt="imagem de cartao" />
                </div>
              </div>
            </div>
          </div>
          <div className="checkout-buttons">
          <Link to="/">
            { /* https://fontawesome.com/icons/long-arrow-alt-left?style=solid */ }
            <i class="fas fa-long-arrow-alt-left"></i>
          </Link>
          <button
            type="submit"
            className="checkout-button"
            onClick= { () => renderConfirm(true) }
          >
            Prosseguir
          </button>
        </div>
        </div>
      </div>
    );
  }
}

Checkout.propTypes = {
  location: propTypes.arrayOf(propTypes.object).isRequired,
  state: propTypes.string.isRequired,
};

const mapStateToProps = (state) => ({
  isToRenderConfirmation: state.details.confirmation.toogle,
});


const mapDispatchToProps = (dispatch) => ({
  renderConfirm: (toogle) => dispatch(renderConfirmation(toogle)),
});

export default connect(mapStateToProps, mapDispatchToProps)(Checkout);
