import { PRODUCT, PRICE } from '../actions';

const INITIAL_STATE = {

};

export default function (state = INITIAL_STATE, action) {
  switch (action.type) {
  case PRODUCT:
    return {
      ...state, product: [...action.product, ...state.product],
    };
  default:
    return state;
  }
}
