import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import Rater from 'react-rater';
import 'react-rater/lib/react-rater.css';
import PropTypes from 'prop-types';
import '../App.css';
import { productPrice, productItem, productDetails, productQuantity } from '../actions';
import { connect } from 'react-redux';

class ProductDetails extends Component {
  constructor() {
    super();
    this.state = {
      quantity: 1,
    };
    this.addItemsToCart = this.addItemsToCart.bind(this);
  }

  addItemsToCart() {
    const { product, addItemCart, dispatchPrice, dispatchProduct, dispatchQuantity, globalID } = this.props;
    const { title, thumbnail, price, id } = product;
    const { quantity } = this.state;
